mapper.select:
SELECT 

FROM s_pm_introduce_opinion a

WHERE a.id = #{id}

SELECT
FROM s_pm_introduce_opinion a
WHERE a.del_flag = '0' AND a.pro_id = #{proId}

SELECT 

FROM s_pm_introduce_opinion a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM s_pm_introduce_opinion a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_pm_introduce_opinion SET 	
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE s_pm_introduce_opinion SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
